sys_script_include.script:
var depNow_kickOff = Class.create();
depNow_kickOff.prototype = Object.extendsObject(AbstractAjaxProcessor, {
	commitUpdateSet : function(){
		var URI = "https://"+gs.getProperty('instance_name')+".service-now.com";
		gs.include("UpdateSetPreviewer");
		gs.include("UpdateSetPreviewAjax");
		var updateRecordSysID = this.getParameter("sysparm_updateSetRecordJSON");
		var responseJSON = [];
		var request = new sn_ws.RESTMessageV2();
		request.setEndpoint(URI+'/api/135654/depnow_commitapi/commit');
		request.setHttpMethod('GET');
		var user = gs.getProperty('depNow_user'); // 'integration.deployer';
		var password = gs.getProperty('depNow_password'); //'integration@1234';
		request.setQueryParameter("queryString",updateRecordSysID);
		request.setBasicAuth(user,password);
		request.setRequestHeader("Accept","application/json");		
		var response = request.execute();
		return response.getBody();
	},
	type: 'depNow_kickOff'
});
